mapper.select:
select count(userId)
from members
where userId = #{userId}

select userId, userName, userPwd, emailId, emailDomain, joinDate
from member
where userId = #{userid} and userPwd = #{userpwd}

select userId, userName, userPwd, emailId, emailDomain, joinDate
from member
where userId = #{userId}

mapper.update:
