mapper.select:
select articleno, userid, subject, hit,
case when date_format(regtime, '%Y%m%d') = date_format(now(), '%Y%m%d')
then date_format(regtime, '%H:%i:%s')
else date_format(regtime, '%y.%m.%d')
end regtime
from board


		where subject like concat('%', #{word}, '%')


		where ${key} = #{word}


order by articleno desc
limit #{start}, #{spp}

select count(articleno) from board
where subject like concat('%', #{word}, '%')
where ${key} = #{word}

mapper.update:
update board
set subject = #{subject}, content =
#{content}
where articleno = #{articleno}

update comment
set content = #{content}
where commentno
= #{commentno}
